mapper.select:
select
,
s.supplier_name,
u.username as purchaser_name
from purchase_order po
left join supplier s on po.supplier_id = s.id
left join sys_user u on po.purchaser_id = u.id


        and po.order_no like concat('%', #{orderNo}, '%')


        and po.supplier_id = #{supplierId}


        and po.status = #{status}


        and po.purchase_date >= #{startDate}


        and po.purchase_date <= #{endDate}


order by po.create_time desc

mapper.insert:
insert into purchase_order (
    order_no, supplier_id, purchaser_id, purchase_date, total_amount,
    status, remark
) values (
    #{orderNo}, #{supplierId}, #{purchaserId}, #{purchaseDate}, #{totalAmount},
    #{status}, #{remark}
)

mapper.update:
update purchase_order
supplier_id = #{supplierId}, purchaser_id = #{purchaserId}, purchase_date = #{purchaseDate}, total_amount = #{totalAmount}, status = #{status}, remark = #{remark},
where id = #{id}

update purchase_order
set status = #{status}
where id = #{id}
