mapper.select:
select
,
c.customer_name,
u.username as seller_name
from sales_order so
left join customer c on so.customer_id = c.id
left join sys_user u on so.seller_id = u.id


        and so.order_no like concat('%', #{orderNo}, '%')


        and so.customer_id = #{customerId}


        and so.status = #{status}


        and so.sales_date >= #{startDate}


        and so.sales_date <= #{endDate}


order by so.create_time desc

mapper.insert:
insert into sales_order (
    order_no, customer_id, seller_id, sales_date, total_amount,
    status, remark
) values (
    #{orderNo}, #{customerId}, #{sellerId}, #{salesDate}, #{totalAmount},
    #{status}, #{remark}
)

mapper.update:
update sales_order
customer_id = #{customerId}, seller_id = #{sellerId}, sales_date = #{salesDate}, total_amount = #{totalAmount}, status = #{status}, remark = #{remark},
where id = #{id}

update sales_order
set status = #{status}
where id = #{id}
